import React, { useState } from 'react';
import { FoodCategories } from "./Foodcategories";
/*import './Foodmenu.css';*/
import './Dropdown.css';


const WindowNavbar = () => {
    const [clicked, setClicked] = useState(false);

    const handleClick = () => {
        setClicked(!clicked);
    }

    return(
        <nav className="DropdownItems">

            <div className="dropdown-icon" onClick={handleClick}>
                <i className={clicked ? 'fas fa-angle-down' : 'fas fa-angle-left'}></i>
            </div>
            <ul className={clicked ? 'dropdown-menu active' : 'dropdown-menu'}>
                {FoodCategories.map((item, index) => {
                    return (
                        <li key={index}>
                            <a className={item.cName} href={item.url}>
                                {item.title}
                            </a>
                        </li>
                    )
                })}
            </ul>
        </nav>
    )
};

export default WindowNavbar;
