import React, { useState, useEffect } from 'react';
import Papa from 'papaparse';
import {useCSVReader} from 'react-papaparse';
import { FoodCategories } from '../Categories/Foodcategories';
import './Foodanddrinks.css';


const Foodanddrinks = (cat) => {
    const [fullMenu, setFullMenu] = useState(null);
    const [ text, setText ] = useState(null);
    let result = {};
    let subCat = "";

    const load = async () => {
        result = await fetch('./fooddrinkdata.csv').then(res => res.text());
        let test = await Papa.parse( result,  {header: true,});
        setText(test);
        setFullMenu(test);  
    };

    useEffect(() => {
            load();
            subCat = "";   
      }, []);


    return (text && (

        <div className='foodDisplay'>
            {fullMenu.data.filter(food1 => food1.value === cat.currentCategory.value)
            .map(food2 => {
                let newCat = false;
                subCat != food2.subcategory ? newCat = true : newCat = false;

                subCat = food2.subcategory;
                return (
                    <React.Fragment>
                        {newCat && <h3 className='subCat'>{food2.subcategory}</h3>}

                        { subCat != 'Nigiri' ?
                            <ul >

                                <li key={food2.name} className='foodItem'>      
                                    <span className='foodName'>{food2.name}</span>
                                    <span className='price'>{food2.price}</span>
                                    <p>{food2.descr}</p>
                                </li>
                            </ul> : 
                            <ul >

                            <li key={food2.name} className='foodItem'>      
                                <span className='foodName'>{food2.name}</span>
                                <span className='price'>{food2.price}</span>
                                <p>{food2.descr}</p>
                            </li>
                        </ul>
                        }

                    </React.Fragment>
                )})
            }

        </div>
    ));



};

export default Foodanddrinks;
